document.addEventListener("DOMContentLoaded",(function(){function RoomPriceConverter(selector){let number=$(selector).text(),decimals=0,decpoint=",",thousand=".",n=Math.abs(number).toFixed(0).split(".");n[0]=n[0].split("").reverse().map((c,i,a)=>i>0&&i<a.length&&i%3==0?c+".":c).reverse().join("");let final=(Math.sign(number)<0?"-":"")+n.join(",");$(selector).text(final)}let roomPriceElements;document.querySelectorAll(".room-price").forEach((function(element){RoomPriceConverter(element)}))}));